(6194, 309, '_why_choose_us_3_icon', 'field_67c7ca33d7ace'),
(6195, 309, 'why_choose_us_3_why_contents', 'Your vision is our priority. We work closely with you throughout the entire process, ensuring your input is valued and your expectations are exceeded. We believe in open communication and building strong, collaborative partnerships.'),
(6196, 309, '_why_choose_us_3_why_contents', 'field_67c7ca3dd7acf'),
(6197, 309, 'why_choose_us_4_name_w', 'Data-Driven Results'),
(6198, 309, '_why_choose_us_4_name_w', 'field_67c7ca1bd7acd'),
(6199, 309, 'why_choose_us_4_icon', ''),
(6200, 309, '_why_choose_us_4_icon', 'field_67c7ca33d7ace'),
(6201, 309, 'why_choose_us_4_why_contents', 'We don\'t just create beautiful videos; we create videos that deliver results. We track key metrics, analyze audience engagement, and optimize your video content to achieve your marketing goals and maximize your return on investment.'),
(6202, 309, '_why_choose_us_4_why_contents', 'field_67c7ca3dd7acf'),
(6203, 309, 'why_choose_us_5_name_w', 'Cutting-Edge Technology'),
