(6212, 309, '_our_process_0_picon', 'field_67c7d09189281'),
(6213, 309, 'our_process_0_pcontent', 'We begin by understanding your vision and goals. We collaborate with you to develop a compelling concept and create detailed storyboards that visualize the narrative and flow of your video. This crucial stage lays the foundation for a successful production.'),
(6214, 309, '_our_process_0_pcontent', 'field_67c7d09b89282');
