(8477, 342, '_all_service_0_all_service_images', 'field_67c69b0d1cfee'),
(8478, 342, 'all_service_1_all_service_title', '3D Animation & High-End Visual Effects (VFX)'),
(8479, 342, '_all_service_1_all_service_title', 'field_67c69ae61cfec'),
(8480, 342, 'all_service_1_all_service_content', 'Bring your visions to life with Nexuslinc\'s 3D animation and high-end visual effects (VFX) services. We specialize in creating 3D animations for various applications, from product visualizations to character animation. Whether you need to add realistic visual effects to live-action footage, create immersive 3D environments, or develop intricate character animations, we deliver high-quality 3D animation and VFX that elevate your projects and leave a lasting impression.'),
(8481, 342, '_all_service_1_all_service_content', 'field_67c69afe1cfed'),
