(8483, 342, '_all_service_1_all_service_images', 'field_67c69b0d1cfee'),
(8484, 342, 'all_service_2_all_service_title', 'Motion Graphics & CGI'),
(8485, 342, '_all_service_2_all_service_title', 'field_67c69ae61cfec'),
