(12227, 464, '_our_process_3_picon', 'field_67c7d09189281'),
(12228, 464, 'our_process_3_pcontent', 'Our experienced team ensures flawless execution on the day of the event. We manage all on-site operations, coordinating vendors, managing timelines, and addressing any unforeseen challenges with professionalism and efficiency.'),
(12229, 464, '_our_process_3_pcontent', 'field_67c7d09b89282'),
(12230, 464, 'our_process_4_processname', 'Hybrid & Virtual Integration'),
(12231, 464, '_our_process_4_processname', 'field_67c7d07b89280'),
(12232, 464, 'our_process_4_picon', ''),
(12233, 464, '_our_process_4_picon', 'field_67c7d09189281'),
(12234, 464, 'our_process_4_pcontent', 'For hybrid or virtual events, we integrate cutting-edge technology to create engaging online experiences. We manage virtual platforms, facilitate live streaming, and ensure seamless interaction between virtual and in-person attendees.'),
(12235, 464, '_our_process_4_pcontent', 'field_67c7d09b89282'),
(12236, 464, 'our_process_5_processname', 'Post-Event Analysis & Reporting'),
(12237, 464, '_our_process_5_processname', 'field_67c7d07b89280'),
